import React from 'react';
import "./ItemList.css";
import Item from "../Item/Item";

export default function ItemList({items,gridcolumns:Number}) {

    let columns = gridcolumns == 4 ? "1fr 1fr 1fr 1fr" : "1fr 1fr";
    const gridColumnsStyle = {"grid-template-columns":  columns};

    return (
        <section id="listedItems" style={gridColumnsStyle}>
            {items.map((item , index) =>
                
                <Item id={`itemList${index}`} key={index} item = {item}></Item>
            )}
        </section>
    )
}
