import React from 'react';
import {useEffect,useState} from 'react';
import { useParams } from 'react-router-dom';
import "./Products.css";
export default function Product() {
    
    //id represents the category of the products to be displayed 
    const {id} = useParams();

    //productsOrder can be : standard , price ascending , price descending .
    //@by default the value is set to be "standard";
    const [productsOrder,setProductsOrder] = useState("standard");

    //products number represents the number of the products that need to be displayed , the value can be 20, 30 or 40 .
    // @By default the value is set to be 20
    const[productsNumber,setProductsNumer] = useState(20);

    //productsPreferredBrand represents an array of brands that are selected as preferred brands , the value can contain 0 or more elements .
    //@By default the array contains 0 elemnts and in this situation, from the server side we will recive products from all the posssible brands.
    const[productsPreferredBrand,setproductsPreferredBrand] = useState([]);

    
    return (
        <div>
            {id}
        </div>
    )
}
