import React from 'react'

export default function InputSlider({ranges:Object , step , setPreferredLowestPrice , setPreferredHighestPrice }) {
    return (
        <div id="inputSlider">
              <input id="ppInputAscending" type="range" min="0" max="999"  step="5" onChange={(e) =>  setProductsPriceRange({lowestPrice : Number.parseInt(e.target.value) ,highestPrice : productsPriceRange.highestPrice})}/>
              <input id="ppInputDescending" type="range" min="0" max="999" step='5' onChange={(e) =>  setProductsPriceRange({lowestPrice : productsPriceRange.lowestPrice ,highestPrice : Number.parseInt(e.target.value)})}/>
        </div>
    )
}
