import React from 'react'
import "./SaleActions.css";
import { useState, useEffect } from 'react';


function getWindowDimensions() {
  const { innerWidth: width, innerHeight: height } = window;
  return {
    width,
    height
  };
}



export default function SaleActions() {
    const[windowDimensions,setWindowDimensions] = useState(getWindowDimensions());
    
    function getImgs()
    {
        if(windowDimensions.width <= 768)
        {
            return ["https://lp2.hm.com/hmgoepprod?set=source[/7e/bd/7ebd3c64f2f07b0bc3642d88c407093a7f4c5154.jpg],origin[dam],category[],type[LOOKBOOK],res[z],hmver[1]&call=url[file:/product/main]",
                "https://lp2.hm.com/hmgoepprod?set=quality%5B79%5D%2Csource%5B%2Fcb%2F61%2Fcb619b695ecbeaeffc1be64e04ff80a674e65ef8.jpg%5D%2Corigin%5Bdam%5D%2Ccategory%5B%5D%2Ctype%5BLOOKBOOK%5D%2Cres%5Bm%5D%2Chmver%5B1%5D&call=url[file:/product/main]",
                "https://lp2.hm.com/hmgoepprod?set=source[/19/ef/19ef7a4bae2bc5db23302ff8bc14673b259e54cf.jpg],origin[dam],category[men_cardigansjumpers_jumpers],type[LOOKBOOK],res[z],hmver[1]&call=url[file:/product/main]"];
        }

        return [
            "https://www.sport-m.com.mk/HomeBanner/001/SM1-3.png",
            "https://sport-m.com.mk/HomeBanner/001/SM1-5.png"];
    }
    
    return (

        <div id="saleAction">
        <div id="carouselExampleIndicators" class="carousel slide" data-bs-ride="carousel">
                <div class="carousel-indicators">
                    <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="0" class="active" aria-current="true" aria-label="Slide 1"></button>
                    <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="1" aria-label="Slide 2"></button>
                    <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="2" aria-label="Slide 3"></button>
                </div>

                <div class="carousel-inner">

                    {(getImgs()).map((item,index) => 
    
                        <div  key={index}class="carousel-item" data-bs-interval="2000">
                            <img src={item} class="d-block w-100" alt="..."/>
                            <div class="carousel-caption  d-md-block">
                                <h5>Second slide label</h5>
                                <p>Some representative placeholder content for the second slide.</p>
                            </div>
                        </div>
                        
                    
                    )}




                    {/* <div class="carousel-item" data-bs-interval="2000">
                    <img src="" class="d-block w-100" alt="..."/>
                    <div class="carousel-caption  d-md-block">
                        <h5>Second slide label</h5>
                        <p>Some representative placeholder content for the second slide.</p>
                    </div>
                    </div>

                    <div class="carousel-item" data-bs-interval="2000">
                    <img src="" class="d-block w-100" alt="..."/>
                    <div class="carousel-caption  d-md-block">
                        <h5>Second slide label</h5>
                        <p>Some representative placeholder content for the second slide.</p>
                    </div>
                    </div> */}

                </div>

        </div>
        </div>
    )
}
