import './App.css';
import Container from './components/Container/Container';
import Input from './components/Input/Input';

import { useState } from 'react';
import axios from 'axios';


const weatherApi = {
 // http://api.weatherapi.com/v1/current.json?q=Skopje&key=9c1199b65355418b8fb150557211312
  url: `https://api.weatherapi.com/v1/forecast.json`,
  key: `9c1199b65355418b8fb150557211312`,
  createUrl : function  (city,days) 
  {return `${this.url}?key=${this.key}&q=${city}&days=${days}`;}
}

//const forecast = {

//}

function App() {
  const [city,setCity] = useState("Skopje");
  const [days,setDays] = useState(1); 
  const [weather,setWeather] = useState([{ city: city,temp: '20'}]);

  function search(c,days=1)
  {
    setCity(c);
    setDays(days);
    axios.get(weatherApi.createUrl(city,days)).then(
      // data => setWeather({
      // city: data.location.name,
      // temp: data.current.temp_c
    // }
    resp => console.log(resp.data)
    ).catch(error => {
      console.log(error)
    });
  }



  return (
    <section className="App">
     <Input search={search}></Input>
      <Container className="main" list={weather}>
      </Container>
    </section>
  );
}

export default App;
