import React from 'react';
import ItemList from '../ItemList/ItemList';
import "./RecommendedItems.css";

const getItems = () =>
    [
        {name: 'addidas shoe',image: "https://sport-m.com.mk/Image/Show?Pateka=%2FProductImages%2FFV7957.jpg", description : {short : 'shoe for these cold times' , details : "..."}},
        {name: 'addidas jacket',image: "https://sport-m.com.mk/Image/Show?Pateka=%2FProductImages%2FGT1699.jpg", description : {short : 'ideal jacket for you' , details : "..."}}
    ]


export default function RecommendedItems() {
    return (
        <div>
            

            <div id="moreItems">
                <h1 id='moreItemsText'>Our recommendations</h1>
                <div id="moreItemsController" data-bs-toggle="collapse" href="#multiCollapseExample1" role="button" aria-expanded="false" aria-controls="itemList">
                <svg viewBox="0 0 24 26" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <circle cx="12" cy="12" r="12" transform="rotate(90 12 12)" fill="#A41A1A"/>
                    <path d="M6 11L12 17L18 11" stroke="white" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                </svg>
                </div>

                <ItemList items = {getItems()}></ItemList>
               
            </div>
        </div>
    )
}
