import React from 'react'
import "./Item.css";
import {Link} from 'react-router-dom';
export default function Item({item}) {
    return (
       
// {id: "a723", brand : "Adidas" , name: 'addidas shoe', price : "130$" , image: "https://sport-m.com.mk/Image/Show?Pateka=%2FProductImages%2FFV7957.jpg", description : {short : 'shoe for these cold times' , details : ""} , colors : ["red" , "black" , "gray" ,"white"] , sizes : ["s","m","xl"]}
    
        <div class="itemCard">
        <Link key={item.id} to={"/product/" + item.id} className="listStyle">
                <div className="itemImg" style={{backgroundImage: `url("${item.image}")`}}  alt=""/>
        </Link>
                <div className="itemData">
                    <div className="itemPrice">
                        <div>{item.price}</div>
    
                    </div>
        <Link key={item.id} to={"/product/" + item.id} className="listStyle">
                    <div className="itemName">{item.name}</div>
                    <div className="itemDescription">{item.description.short}</div>
        </Link>
                </div>
                
        </div>
    )
}
