import React from 'react';
import './Select.css';

export default function Select({labelText,onChangeFunction,name,options}) {
    return (
        <div>
            <label>{labelText}</label>
            
            <select name="sort" id="sortProducts" onChange={(e) => window.alert(e.target.value)}>
                        {/* <option className="optionValue" value="standard">Standard</option>
                        <option className="optionValue" value="price_ascending">Price ascending</option>
                        <option className="optionValue" value="price_descending">Price descending</option> */}
                        {options.map((option , index) => <option value={option.value}>{option.text}</option> )}
            </select>
        </div>
    )
}
