import './App.css';
import Container from './components/Container/Container';
import Input from './components/Input/Input';
import Widget from './components/Widget/Widget';
import { useState } from 'react';
import axios from 'axios';


const weatherApi = {
 // http://api.weatherapi.com/v1/current.json?q=Skopje&key=9c1199b65355418b8fb150557211312
  url: `http://api.weatherapi.com/v1/current.json`,
  key: `9c1199b65355418b8fb150557211312`,
  createUrl : function  (city) 
  {return `${this.url}?q=${city}&key=${this.key}`;}
}

function App() {
  const [city,setCity] = useState("Skopje");
  const [weather,setWeather] = useState({ city: 'skopje',temp: '20'});

  function search(city)
  {

    setCity(city);
    // console.log(weatherApi.createUrl(city));
    // console.log(`https://samples.openweathermap.org/data/2.5/find?q=London&appid=5eab5503f4297b3fc538ea2f8f796e07`);
    axios.get(weatherApi.createUrl(city)).then(data => setWeather({
      city: data.location.name,
      temp: data.current.temp_c
    })).catch(error => {
      console.log(error)
    });
  }





  return (
    <>
      <Container className="main">
        <Input search={search}></Input>
        <Widget weather={weather}></Widget>
      </Container>
    </>
  );
}

export default App;
