import axios from 'axios';


const weatherApi = {
  url: `https://samples.openweathermap.org/data/2.5/`,
  appid: `5eab5503f4297b3fc538ea2f8f796e07`,
  createUrl : function  (city) 
  {return `${this.url}?q=${city}&appid${this.appid}`;}
}

function App() {
  const [city,setCity] = useState("Skopje");
  const [weather,setWeather] = useState({});

  function search(city)
  {
    setCity(city);
    axios.get(weatherApi.createUrl)

  }


  return (
    <>
      <Container className="main">
        <Input search={search}></Input>
        <Widget weather={weather} ></Widget>
      </Container>
    </>
  );
}

export default App;
