import React from 'react'
import './Widget.css'
import { useState } from 'react';
export default function Widget({daysummary,alt}) {

    return (
        <div className="widget">
           <img src = {+daysummary.icon} alt={alt}/>
           <div className="statistics">
                <p>Maximum day temperature: {daysummary.maxtemp_c} C</p>
                <h6>Average day temperature {daysummary.avgtemp_c}</h6>
                <p>Minimum day temperature: {daysummary.mintemp_c} C</p>
           </div>
        </div>
    )
}
